mapper.insert:
INSERT INTO cart (
	user_id,
	product_id
) VALUES (
    #{userId},
    #{productId}
)ON DUPLICATE KEY 
UPDATE
quantity = quantity + 1;

mapper.update:
    UPDATE cart
	SET
    quantity = #{quantity}
	WHERE product_id = #{productId}
    AND user_id=#{userId}

mapper.delete:
DELETE FROM cart
WHERE user_id = #{userId}
AND product_id IN

    #{productId}
