mapper.select:
SELECT sub.order_id, sub.total_price, sub.status, sub.post_code , sub.road_address , sub.jibun_address , sub.detail_address , sub.create_date, oi.order_item_id, CONCAT(f.file_id, '.', f.file_type) AS image_url, p.name as product_name, oi.quantity, oi.price, oi.status as orderItemStatus, oi.create_date AS oi_create_date FROM ( SELECT o.order_id, o.total_price, o.status, o.post_code , o.road_address , o.jibun_address , o.detail_address , o.create_date FROM orders o WHERE o.user_id=#{userId}
AND o.status = #{status}
AND o.order_id < #{orderId}
ORDER BY o.order_id DESC LIMIT #{limit} ) sub INNER JOIN orderItems oi ON sub.order_id = oi.order_id INNER JOIN product p ON p.product_id =oi.product_id LEFT OUTER JOIN file f ON oi.product_id=f.reference_id AND f.reference_type='PRODUCT_IMG' AND f.use_yn='Y'
