mapper.select:
SELECT p.product_id, p.name, p.description, p.price, p.category_id, CONCAT(f.file_id, '.', f.file_type) AS imageUrl, p.create_date FROM product p LEFT OUTER JOIN file f ON p.product_id=f.reference_id AND f.reference_type='PRODUCT_IMG' AND f.use_yn='Y'
AND p.use_yn = 'Y'
AND p.product_id < #{productId}
AND p.category_id = #{categoryId}
ORDER BY p.create_date DESC, p.product_id DESC LIMIT #{limit}
